import './App.css';
import ButtonAppBar from './ButtonAppBar';
import { Paper, FormControl, Button } from '@mui/material';
import { useEffect, useState } from 'react';

export default function App() {
  const paperStyle = {
    width: '700px',
    height: '350px',
    marginTop: '40px'
  }
  const [todos, setTodos] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8080/todos/getAll')
      .then(data => data.json())
      .then((data) => {setTodos(data)});
  }, []);

  function handleAdd() {
    console.log('Add');
    console.log(todos);
  }

  return (
    <div className="App">
      <ButtonAppBar />
      <FormControl>
        <Paper elevation={3} style={paperStyle}>
          {
            todos.map(todo => (
              <Paper key={todo.id}>
                Name of todo: {todo.name}
                Description: {todo.description}
                Added on: {todo.added_on}
                Completed: {todo.completed}
              </Paper>
            ))
          }
        </Paper>
        <Button variant='contained' onClick={handleAdd}>Fetch data</Button>
      </FormControl>
    </div>
  );
}
