import './App.css';
import ButtonAppBar from './ButtonAppBar';
import { Paper, FormControl, Button } from '@mui/material';
import { useEffect, useState } from 'react';

export default function App() {
  const mainPaperStyle = {
    width: '700px',
    height: '450px',
    marginTop: '40px'
  }
  const subPaperStyle = {
    width: '250px',
    height: '150px',
    padding: '30px 50px',
    margin: '30px 15px'
  }

  const [todos, setTodos] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8080/todos/getAll')
      .then(data => data.json())
      .then((data) => {setTodos(data)});
  }, []);

  function handleAdd() {
    console.log('Add');
    console.log(todos);
  }

  return (
    <div className="App">
      <ButtonAppBar />
      <FormControl>
        <Paper elevation={3} style={mainPaperStyle}>
          {
            todos.map(todo => (
              <Paper elevation={2} key={todo.id} style={subPaperStyle}>
                Name of todo: {todo.name} <br/>
                Description: {todo.description} <br/>
                Added on: {todo.added_on} <br/>
                Completed: {todo.completed} <br/>
              </Paper>
            ))
          }
        </Paper>
        <Button variant='contained' onClick={handleAdd}>Fetch data</Button>
      </FormControl>
    </div>
  );
}
