import './App.css';
import ButtonAppBar from './ButtonAppBar';
import MediaCard from './MediaCard';
import { Paper, FormControl, Button} from '@mui/material';
import { useEffect, useState } from 'react';

export default function App() {
  const mainPaperStyle = {
    width: '700px',
    height: '580px',
    marginTop: '40px',
  }
  const subPaperStyle = {
    width: '250px',
    height: '90px',
    padding: '30px 50px',
    margin: '0 auto',
    paddingTop: '35px'
  }

  const [todos, setTodos] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8080/todos/getAll')
      .then(data => data.json())
      .then((data) => {setTodos(data)});
  }, []);

  function handleAdd() {
    console.log('Add');
    console.log(todos);
  }

  return (
    <div className="App">
      <ButtonAppBar />
      <FormControl>
        <Paper elevation={3} style={mainPaperStyle}>
          {
            todos.map(todo => (
              <MediaCard 
                key={todo.id}
                name={todo.name}
                description={todo.description}
                addedOn={todo.added_on}
                completed={todo.completed}
              />
            ))
          }
        </Paper>
        <Button variant='contained' onClick={handleAdd} style={{width: '70px', height: '30px'}}>Add todo</Button>
      </FormControl>
    </div>
  );
}

